class ImageList(object):
    def __init__(self, img_array,text_array):
        self.imgs =[(img_array[i],text_array[i]) for i in range(len(img_array))]

    def __getitem__(self, index):
        img,text = self.imgs[index]
        return img, text,index

    def __len__(self):
        return len(self.imgs)
    
class TestList(object):
    def __init__(self, img,label):
        self.imgs = [(img[i], label[i]) for i in range(len(img))]

    def __getitem__(self, index):
        img, target = self.imgs[index]
        return img, target, index

    def __len__(self):
        return len(self.imgs)
    

