hash_targets = get_hash_targets(len(community75), 64).to(config["device"])

def label3center(tt,ind,bit):
    hash_center=torch.zeros((tt.shape[0], bit))
    for i in range(tt.shape[0]):
        for j in range(len(community75)):
            if ind[i] in community75[j]:
                hash_center[i]=hash_targets[j]
    return hash_center.to(config["device"])


# ================= 训练与检索 =================
def train_val(config, bit):
    criterion = nn.MSELoss()
    input_dim = 512
    hidden_dim=128
    output_dim = 64 

    model = MLP(input_dim, hidden_dim,output_dim).to(device)
    optimizer = config["optimizer"]["type"](model.parameters(), **(config["optimizer"]["optim_params"]))
    Best_mAP = 0

    for epoch in range(config["epoch"]):
        current_time = time.strftime('%H:%M:%S', time.localtime(time.time()))
        print("%s[%2d/%2d][%s] bit:%d, dataset:%s, training...." % (
            config["info"], epoch + 1, config["epoch"], current_time, bit, config["dataset"]), end="")
        
        model.train()
        train_loss = 0
        for img_data,text_data,ind in train_loader:
            img_data=img_data.to(device).to(torch.float32)
            text_data=text_data.to(device)
            S1= F.cosine_similarity(img_data.unsqueeze(1),img_data.unsqueeze(0),dim=-1)
            S2=F.cosine_similarity(text_data.unsqueeze(1),text_data.unsqueeze(0),dim=-1)
            hash_center=label3center(img_data,ind,bit)

            optimizer.zero_grad()
            outputs = model(img_data)
            S_IMG=F.cosine_similarity(outputs.unsqueeze(1),outputs.unsqueeze(0),dim=-1)
            S=S1+0.01*S2

            loss1 = criterion(S_IMG, S)
            loss2=criterion(outputs,hash_center)
            loss=loss1+loss2
            loss.backward()
            optimizer.step()
            train_loss += loss.item()

        train_loss = train_loss / len(train_loader)
        print("\b\b\b\b\b\b\b loss:%.3f" % (train_loss))

        if (epoch + 1) % config["test_map"] == 0:
            tst_binary, tst_label = compute_result(test_loader, model, device=device)
            trn_binary, trn_label = compute_result(db_loader, model, device=device)

            mAP = CalcTopMap(trn_binary.numpy(), tst_binary.numpy(), trn_label.numpy(), tst_label.numpy(),
                             config["topK"])
                             
            if mAP > Best_mAP:
                Best_mAP = mAP
                if "save_path" in config:
                    if not os.path.exists(config["save_path"]):
                        os.makedirs(config["save_path"])
                    print("save in ", config["save_path"])

                    np.save(os.path.join(config["save_path"], config["dataset"] + str(bit) + "-"  + "trn_binary.npy"),
                            trn_binary.numpy())
                    np.save(os.path.join(config["save_path"], config["dataset"] + str(bit) + "-"  + "tst_binary.npy"),
                            tst_binary.numpy())
                    np.save(os.path.join(config["save_path"], config["dataset"] + str(bit) + "-"  + "trn_label.npy"),
                            trn_label.numpy())
                    np.save(os.path.join(config["save_path"], config["dataset"] + str(bit) + "-"  + "tst_label.npy"),
                            tst_label.numpy())
                    np.save(os.path.join(config["save_path"], config["dataset"] + str(bit) +"-" + "map.npy"),
                            mAP)

            print("%s epoch:%d, bit:%d, dataset:%s, MAP:%.3f, Best MAP: %.3f" %
                  (config["info"], epoch + 1, bit, config["dataset"], mAP, Best_mAP))
            print(config)


if __name__ == "__main__":
    config = get_config()
    print(config)
    for bit in config["bit_list"]:
        train_val(config, bit)   # 指定测试集索引
